"use client"
import axios from "axios"; 

import type React from "react"
import { useState, useEffect, useRef } from "react"
import { ArrowLeft, Save } from 'lucide-react';
// Use a relative import path instead of absolute path for better portability
import "../assets/Create_template.css"
import {
  ChevronDown,
  ChevronUp,
  Edit,
  Plus,
  Calendar,
  User,
  MapPin,
  X,
  Check,
  Image,
  Trash2,
  Move,
  ExternalLink,
  Clock,
} from "lucide-react"

// Types
type ResponseType =
  | "Site"
  | "Inspection date"
  | "Person"
  | "Inspection location"
  | "Text"
  | "Number"
  | "Checkbox"
  | "Yes/No"
  | "Multiple choice"
  | "Slider"
  | "Media"
  | "Annotation"
  | "Date & Time"

interface Question {
  id: string;
  text: string;
  responseType: ResponseType;
  required: boolean;
  order?: number;
  section?: string;
  options?: string[];
  value?: string | string[] | boolean | number | null;
}

interface Section {
  id: string;
  title: string;
  description?: string;
  order?: number;
  template?: string;
  isCollapsed: boolean;
  questions: Question[];
}

interface Template {
  id: string;
  title: string;
  description: string;
  sections: Section[];
  lastSaved?: Date;
  lastPublished?: Date;
  logo?: string;
}

// Helper functions
const generateId = () => Math.random().toString(36).substring(2, 9)

const getDefaultQuestion = (responseType: ResponseType = "Text"): Question => ({
  id: generateId(),
  text: "Type question",
  responseType,
  required: false,
  options:
    responseType === "Multiple choice" || responseType === "Yes/No" ? ["Option 1", "Option 2", "Option 3"] : undefined,
  value: null,
})

const getDefaultSection = (title = "Untitled Page"): Section => ({
  id: generateId(),
  title,
  questions: [],
  isCollapsed: false,
})

const getInitialTemplate = (): Template => {
  const titlePageSection: Section = {
    id: generateId(),
    title: "Title Page",
    description: "The Title Page is the first page of your inspection report. You can",
    questions: [
      {
        id: generateId(),
        text: "Site conducted",
        responseType: "Site",
        required: true,
        value: null,
      },
      {
        id: generateId(),
        text: "Conducted on",
        responseType: "Inspection date",
        required: true,
        value: null,
      },
      {
        id: generateId(),
        text: "Prepared by",
        responseType: "Person",
        required: true,
        value: null,
      },
      {
        id: generateId(),
        text: "Location",
        responseType: "Inspection location",
        required: true,
        value: null,
      },
    ],
    isCollapsed: false,
  }

  return {
    id: generateId(),
    title: "Untitled template",
    description: "Add a description",
    sections: [titlePageSection],
    lastSaved: new Date(),
    lastPublished: new Date(),
    logo: undefined,
  }
}

// Main component
const Create_template = () => {
  const [template, setTemplate] = useState<Template>(getInitialTemplate())
  const [activeTab, setActiveTab] = useState<number>(1)
  const [activeSectionId, setActiveSectionId] = useState<string | null>(null)
  const [activeQuestionId, setActiveQuestionId] = useState<string | null>(null)
  const [draggedItem, setDraggedItem] = useState<{ type: "question" | "section"; id: string } | null>(null)
  const [dropTarget, setDropTarget] = useState<{ type: "question" | "section"; id: string } | null>(null)
  const [showResponseTypeMenu, setShowResponseTypeMenu] = useState<string | null>(null)
  const [showMobilePreview, setShowMobilePreview] = useState<boolean>(true)
  const [allChangesSaved, setAllChangesSaved] = useState<boolean>(true)
  const [templateData, setTemplateData] = useState<any[]>([])
  const [loading, setLoading] = useState<boolean>(true)
  const [error, setError] = useState<string | null>(null)

  // Refs
  const questionRefs = useRef<{ [key: string]: HTMLDivElement | null }>({})
  const sectionRefs = useRef<{ [key: string]: HTMLDivElement | null }>({})

  useEffect(() => {
    const fetchTemplates = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/users/create_templates/");
        setTemplateData(response.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching templates:", err);
        setError(err instanceof Error ? err.message : "Failed to load template data");
        setLoading(false);
      }
    };
    fetchTemplates();
  }, []);

  useEffect(() => {
    const saveTimer = setTimeout(() => {
      localStorage.setItem("safetyTemplate", JSON.stringify(template))
      setAllChangesSaved(true)
      setTemplate((prev) => ({
        ...prev,
        lastSaved: new Date(),
      }))
    }, 1000)

    return () => clearTimeout(saveTimer)
  }, [template])

  // Load template from localStorage
  useEffect(() => {
    const savedTemplate = localStorage.getItem("safetyTemplate")
    if (savedTemplate) {
      try {
        const parsed = JSON.parse(savedTemplate)
        setTemplate(parsed)
      } catch (e) {
        console.error("Failed to parse saved template", e)
      }
    }
  }, [])

  // Mark changes as unsaved when template is modified
  useEffect(() => {
    setAllChangesSaved(false)
  }, [template])

  // Template manipulation functions
  const updateTemplateTitle = (title: string) => {
    setTemplate((prev) => ({ ...prev, title }))
  }

  const updateTemplateDescription = (description: string) => {
    setTemplate((prev) => ({ ...prev, description }))
  }

  const prepareTemplateForSaving = (template: Template): Template => {
    return {
      ...template,
      sections: template.sections.map((section: Section, secIndex: number) => ({
        ...section,
        order: secIndex + 1,
        template: template.id,
        questions: section.questions.map((question: Question, qIndex: number) => ({
          ...question,
          order: qIndex + 1,
          section: section.id,
        })),
      })),
    };
  };

  const updateQuestionOptions = (sectionId: string, questionId: string, optionIndex: number, value: string) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section: Section) => ({
        ...section,
        questions: section.questions.map((question: Question) =>
          question.id === questionId
            ? {
                ...question,
                options: question.options?.map((option: string, idx: number) => (idx === optionIndex ? value : option)),
              }
            : question
        ),
      })),
    }));
  };

  const handleSave = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/api/users/create_templates/", template, {
        headers: { "Content-Type": "application/json" },
      });

      console.log("✅ Template saved successfully:", response.data);
      alert("Template saved successfully!");
      setAllChangesSaved(true);
      setTemplate((prev) => ({
        ...prev,
        lastSaved: new Date(),
      }));
    } catch (error: unknown) {
      if (axios.isAxiosError(error)) {
        console.error("❌ Axios Error saving template:", error.response?.data || error.message);
      } else {
        console.error("❌ Unknown error:", error);
      }
      alert("Failed to save template. Check console for details.");
    }
  };

  // Handle back button
  const handleBack = () => {
    if (window.confirm("Do you want to save before leaving?")) {
      handleSave()
    }
    // Navigate back logic would go here
    console.log("Navigating back...")
  }

  const handleLogoUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("logo", file);

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/upload_logo/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setTemplate((prev) => ({ ...prev, logo: response.data.logo_url }));
    } catch (error) {
      console.error("❌ Error uploading logo:", error);
      alert("Failed to upload logo.");
    }
  };

  // Fixed addSection function
  const addSection = () => {
    const newSection = getDefaultSection()
    setTemplate((prev) => ({
      ...prev,
      sections: [...prev.sections, newSection],
    }))
    setActiveSectionId(newSection.id)
    setTimeout(() => {
      const newSectionElement = document.getElementById(`section-${newSection.id}`)
      if (newSectionElement) {
        newSectionElement.scrollIntoView({ behavior: "smooth", block: "start" })
      }
    }, 100)
  }

  // Fixed toggleSectionCollapse function (which was merged with addSection)
  const toggleSectionCollapse = (sectionId: string) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) =>
        section.id === sectionId
          ? { ...section, isCollapsed: !section.isCollapsed }
          : section
      ),
    }))
  }

  const updateSection = (sectionId: string, updates: Partial<Section>) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) => (section.id === sectionId ? { ...section, ...updates } : section)),
    }))
  }

  // Add question function
  const addQuestion = (sectionId: string, responseType: ResponseType = "Text") => {
    const newQuestion = getDefaultQuestion(responseType)
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) =>
        section.id === sectionId
          ? {
              ...section,
              questions: [...section.questions, newQuestion],
            }
          : section
      ),
    }))
    setActiveQuestionId(newQuestion.id)
  }

  // Update question function
  const updateQuestion = (sectionId: string, questionId: string, updates: Partial<Question>) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) =>
        section.id === sectionId
          ? {
              ...section,
              questions: section.questions.map((question) =>
                question.id === questionId
                  ? { ...question, ...updates }
                  : question
              ),
            }
          : section
      ),
    }))
  }

  // Delete question function
  const deleteQuestion = (sectionId: string, questionId: string) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) =>
        section.id === sectionId
          ? {
              ...section,
              questions: section.questions.filter((q) => q.id !== questionId),
            }
          : section
      ),
    }))
    setActiveQuestionId(null)
  }

  // Delete section function
  const deleteSection = (sectionId: string) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.filter((section) => section.id !== sectionId),
    }))

    if (activeSectionId === sectionId) {
      setActiveSectionId(null)
    }
    if (activeQuestionId) {
      // Check if the active question is in this section
      const sectionWithActiveQuestion = template.sections.find((section) =>
        section.id === sectionId && section.questions.some((q) => q.id === activeQuestionId)
      )
      if (sectionWithActiveQuestion) {
        setActiveQuestionId(null)
      }
    }
  }

  // Function to render response type icon
  const renderResponseTypeIcon = (type: ResponseType) => {
    switch (type) {
      case "Site":
        return <MapPin size={16} />
      case "Inspection date":
        return <Calendar size={16} />
      case "Person":
        return <User size={16} />
      case "Inspection location":
        return <MapPin size={16} />
      case "Text":
        return <Edit size={16} />
      case "Number":
        return <span className="icon-number">#</span>
      case "Checkbox":
        return <Check size={16} />
      case "Yes/No":
        return <span className="icon-yesno">Y/N</span>
      case "Multiple choice":
        return <span className="icon-multi">☑</span>
      case "Slider":
        return <span className="icon-slider">⟷</span>
      case "Media":
        return <Image size={16} />
      case "Annotation":
        return <Edit size={16} />
      case "Date & Time":
        return <Clock size={16} />
      default:
        return <span>?</span>
    }
  }

  // Render component (add your JSX here)
  return (
    <div className="template-editor">
      <div className="header">
        <div className="nav-left">
          <div className="company-name">FASHCOGNITIVE</div>
          <button className="back-button" onClick={handleBack}>
            <ArrowLeft size={16} />
            <span>back</span>
          </button>
        </div>

        <div className="nav-right">
          <button 
            className="save-button" 
            onClick={handleSave}
            disabled={allChangesSaved}
          >
            <Save size={16} />
            <span>{allChangesSaved ? "Saved" : "Save"}</span>
          </button>
        </div>
      </div>

      <div className="editor-content">
        {/* Tabs */}
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 1 ? 'active' : ''}`} 
            onClick={() => setActiveTab(1)}
          >
            Builder
          </button>
          <button 
            className={`tab ${activeTab === 2 ? 'active' : ''}`} 
            onClick={() => setActiveTab(2)}
          >
            Preview
          </button>
        </div>

        {/* Builder Tab */}
        {activeTab === 1 && (
          <div className="builder">
            {/* Template settings */}
            <div className="template-settings">
              <input
                type="text"
                value={template.title}
                onChange={(e) => updateTemplateTitle(e.target.value)}
                className="template-title"
                placeholder="Template title"
              />
              <textarea
                value={template.description}
                onChange={(e) => updateTemplateDescription(e.target.value)}
                className="template-description"
                placeholder="Template description"
              />
              <div className="logo-upload">
                <label htmlFor="logo-input" className="logo-label">
                  {template.logo ? (
                    <img src={template.logo} alt="Template logo" className="template-logo" />
                  ) : (
                    <div className="logo-placeholder">
                      <Image size={24} />
                      <span>Upload logo</span>
                    </div>
                  )}
                </label>
                <input
                  id="logo-input"
                  type="file"
                  accept="image/*"
                  onChange={handleLogoUpload}
                  className="logo-input"
                />
              </div>
            </div>

            {/* Sections panel */}
            <div className="sections-panel">
              {template.sections.map((section) => (
                <div 
                  id={`section-${section.id}`}
                  key={section.id} 
                  className={`section ${activeSectionId === section.id ? 'active' : ''}`}
                  ref={(el) => {
                    sectionRefs.current[section.id] = el;
                  }}
                >
                  <div 
                    className="section-header" 
                    onClick={() => toggleSectionCollapse(section.id)}
                  >
                    <div className="section-title">
                      {section.title || "Untitled Section"}
                    </div>
                    {section.isCollapsed ? <ChevronDown size={16} /> : <ChevronUp size={16} />}
                  </div>

                  {!section.isCollapsed && (
                    <div className="section-content">
                      <div className="section-description">
                        {section.description && <p>{section.description}</p>}
                      </div>

                      <div className="questions-list">
                        {section.questions.map((question) => (
                          <div 
                            key={question.id}
                            className={`question-item ${activeQuestionId === question.id ? 'active' : ''}`}
                            onClick={() => setActiveQuestionId(question.id)}
                            ref={(el) => { questionRefs.current[question.id] = el; }}
                          >
                            <div className="question-type-icon">
                              {renderResponseTypeIcon(question.responseType)}
                            </div>
                            <div className="question-text">
                              {question.text || "Untitled Question"}
                            </div>
                            <div className="question-actions">
                              <button 
                                className="delete-button"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  deleteQuestion(section.id, question.id);
                                }}
                              >
                                <Trash2 size={16} />
                              </button>
                            </div>
                          </div>
                        ))}

                        <button 
                          className="add-question-button"
                          onClick={() => addQuestion(section.id)}
                        >
                          <Plus size={16} />
                          <span>Add Question</span>
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              ))}

              <button 
                className="add-section-button"
                onClick={addSection}
              >
                <Plus size={16} />
                <span>Add Section</span>
              </button>
            </div>

            {/* Properties panel */}
            <div className="properties-panel">
              {activeQuestionId && (
                <div className="question-properties">
                  {/* Add question editing UI here */}
                </div>
              )}
              {activeSectionId && !activeQuestionId && (
                <div className="section-properties">
                  {/* Add section editing UI here */}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Preview Tab */}
        {activeTab === 2 && (
          <div className="preview">
            <div className={`preview-container ${showMobilePreview ? 'mobile' : 'desktop'}`}>
              <div className="preview-toggle">
                <button 
                  className={`toggle-button ${showMobilePreview ? '' : 'active'}`}
                  onClick={() => setShowMobilePreview(false)}
                >
                  Desktop
                </button>
                <button 
                  className={`toggle-button ${showMobilePreview ? 'active' : ''}`}
                  onClick={() => setShowMobilePreview(true)}
                >
                  Mobile
                </button>
              </div>

              <div className="preview-content">
                {/* Add template preview rendering here */}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Additional UI elements, modals, etc. */}
    </div>
  );
};

export default Create_template;
