import React, { useState, useEffect } from 'react';
import { ChevronDown, ArrowLeft, Save } from 'lucide-react'; // Add missing imports

// Define types in one place to avoid conflicts
type ResponseType = "Text" | "Number" | "Select" | "MultiSelect" | "YesNo";

interface Question {
  id: string;
  title: string;
  description?: string;
  responseType: ResponseType;
  order?: number;
  section?: string;
  isRequired?: boolean;
  value?: string | number | boolean | string[] | null;
  // Add other properties as needed
}

interface Section {
  id: string;
  title: string;
  description?: string;
  order?: number;
  template?: string;
  isCollapsed?: boolean;
  questions: Question[];
}

interface Template {
  id: string;
  title: string;
  description?: string;
  logo?: string;
  sections: Section[];
}

// Helper functions
const getDefaultQuestion = (responseType: ResponseType): Question => {
  return {
    id: crypto.randomUUID(),
    title: "",
    description: "",
    responseType,
    isRequired: false,
  };
};

const getDefaultSection = (): Section => {
  return {
    id: crypto.randomUUID(),
    title: "New Section",
    description: "",
    questions: [],
    isCollapsed: false,
  };
};

const getInitialTemplate = (): Template => {
  return {
    id: crypto.randomUUID(),
    title: "New Template",
    description: "",
    sections: [],
  };
};

// Main component
const Create_template = () => {
  const [template, setTemplate] = useState<Template>(getInitialTemplate());
  const [activeTab, setActiveTab] = useState<number>(1);
  const [activeSectionId, setActiveSectionId] = useState<string | null>(null);
  const [activeQuestionId, setActiveQuestionId] = useState<string | null>(null);

  // Fixed addSection function
  const addSection = () => {
    const newSection = getDefaultSection();
    setTemplate((prev) => ({
      ...prev,
      sections: [...prev.sections, newSection],
    }));
    setActiveSectionId(newSection.id);
    setTimeout(() => {
      const newSectionElement = document.getElementById(`section-${newSection.id}`);
      if (newSectionElement) {
        newSectionElement.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  // Fixed toggleSectionCollapse function (was merged with addSection)
  const toggleSectionCollapse = (sectionId: string) => {
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) =>
        section.id === sectionId
          ? { ...section, isCollapsed: !section.isCollapsed }
          : section
      ),
    }));
  };

  // Fixed addQuestion function
  const addQuestion = (sectionId: string, responseType: ResponseType = "Text") => {
    const newQuestion = getDefaultQuestion(responseType);
    setTemplate((prev) => ({
      ...prev,
      sections: prev.sections.map((section) =>
        section.id === sectionId
          ? {
              ...section,
              questions: [...section.questions, newQuestion],
            }
          : section
      ),
    }));
    setActiveQuestionId(newQuestion.id);
  };

  // Helper function for rendering response type icons
  const renderResponseTypeIcon = (type: ResponseType) => {
    switch (type) {
      case "Text":
        return <span>T</span>;
      case "Number":
        return <span>N</span>;
      case "Select":
        return <span>S</span>;
      case "MultiSelect":
        return <span>MS</span>;
      case "YesNo":
        return <span>Y/N</span>;
      default:
        return <span>?</span>;
    }
  };

  const handleSave = () => {
    // Your save implementation
    console.log("Saving template:", template);
  };

  const handleBack = () => {
    // Your back navigation implementation
    console.log("Going back");
  };

  // Render component
  return (
    <div className="template-editor">
      <div className="header">
        <div className="nav-left">
          <div className="company-name">FASHCOGNITIVE</div>
          <button className="back-button" onClick={handleBack}>
            <ArrowLeft size={16} />
            <span>back</span>
          </button>
        </div>

        <div className="nav-right">
          <button className="save-button" onClick={handleSave}>
            <Save size={16} />
            <span>Save</span>
          </button>
        </div>
      </div>

      <div className="editor-content">
        {/* Tabs */}
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 1 ? 'active' : ''}`} 
            onClick={() => setActiveTab(1)}
          >
            Builder
          </button>
          <button 
            className={`tab ${activeTab === 2 ? 'active' : ''}`} 
            onClick={() => setActiveTab(2)}
          >
            Preview
          </button>
        </div>

        {/* Builder Tab */}
        {activeTab === 1 && (
          <div className="builder">
            <div className="sections-panel">
              {template.sections.map((section) => (
                <div 
                  id={`section-${section.id}`}
                  key={section.id} 
                  className={`section ${activeSectionId === section.id ? 'active' : ''}`}
                >
                  <div 
                    className="section-header" 
                    onClick={() => toggleSectionCollapse(section.id)}
                  >
                    <span>{section.title || "Untitled Section"}</span>
                    <ChevronDown size={16} />
                  </div>

                  {!section.isCollapsed && (
                    <div className="questions-list">
                      {section.questions.map((question) => (
                        <div 
                          key={question.id}
                          className={`question-item ${activeQuestionId === question.id ? 'active' : ''}`}
                          onClick={() => setActiveQuestionId(question.id)}
                        >
                          <div className="response-type-indicator">
                            {renderResponseTypeIcon(question.responseType as ResponseType)}
                          </div>
                          <div className="question-title">
                            {question.title || "Untitled Question"}
                          </div>
                        </div>
                      ))}
                      <button 
                        className="add-question-button"
                        onClick={() => addQuestion(section.id)}
                      >
                        + Add Question
                      </button>
                    </div>
                  )}
                </div>
              ))}
              <button 
                className="add-section-button"
                onClick={addSection}
              >
                + Add Section
              </button>
            </div>

            {/* Properties panel for editing active question or section */}
            <div className="properties-panel">
              {activeQuestionId && (
                <div className="question-properties">
                  {/* Render question editing UI */}
                </div>
              )}
              {activeSectionId && !activeQuestionId && (
                <div className="section-properties">
                  {/* Render section editing UI */}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Preview Tab */}
        {activeTab === 2 && (
          <div className="preview">
            {/* Template preview rendering */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Create_template;
